// Function to generate dummy watch data
export function generateDummyWatches(count) {
    const brands = ['Citizen', 'Seiko', 'Orient', 'Timex', 'Casio', 'Fossil', 'Daniel Wellington', 'Alexandre Christie', 'Nixon', 'Swatch', 'Guess', 'Alba', 'Garmin', 'Samsung', 'Huawei', 'Fitbit', 'Xiaomi', 'Rolex', 'Omega', 'Tag Heuer', 'Breitling', 'Grand Seiko', 'Nomos', 'Tudor', 'Oris', 'Longines', 'Rado', 'Patek Philippe', 'A. Lange & Söhne', 'Jaeger-LeCoultre', 'Vacheron Constantin', 'Breguet', 'Zenith', 'Hamilton', 'Certina', 'Q&Q', 'VTech', 'Disney', 'Lego', 'Apple', 'Luminox'];
    const types = ['Sport', 'Fashion', 'Classic', 'Kids', 'Smart'];
    const colors = ['Black', 'White', 'Silver', 'Gold', 'Blue', 'Rose Gold', 'Red', 'Green', 'Brown'];
    const caseMaterials = ['Stainless Steel', 'Titanium', 'Ceramic', 'Gold', 'Plastic', 'Resin', 'Aluminum', 'Carbon Compound', 'Polycarbonate'];
    const strapTypes = ['Leather', 'Rubber', 'Stainless Steel', 'NATO', 'Mesh', 'Silicone', 'Canvas', 'Composite Bracelet', 'Bangle', 'Synthetic Leather', 'Nylon'];
    const statuses = ['New', 'Used'];

    const getRandom = (arr) => arr[Math.floor(Math.random() * arr.length)];
    const getRandomPrice = (min, max) => Math.floor(Math.random() * (max - min + 1) + min);
    const getRandomBoolean = () => Math.random() > 0.5;

    let dummyWatches = [];
    for (let i = 0; i < count; i++) {
        const brand = getRandom(brands);
        const type = getRandom(types);
        const isDiscount = getRandomBoolean();
        const status = getRandom(statuses);
        let price, originalPrice = 0;

        if (type === 'Classic' && getRandomBoolean()) { // High-end classic watches
            price = getRandomPrice(50000000, 500000000);
        } else if (type === 'Smart' && getRandomBoolean()) { // Mid-high smartwatches
            price = getRandomPrice(3000000, 10000000);
        } else if (type === 'Kids') { // Low-end kids watches
            price = getRandomPrice(100000, 900000);
        } else { // General range
            price = getRandomPrice(800000, 20000000);
        }

        if (isDiscount) {
            originalPrice = price + getRandomPrice(price * 0.1, price * 0.3); // 10-30% discount
        } else {
            originalPrice = price;
        }

        dummyWatches.push({
            id: `W${(i + 71).toString().padStart(3, '0')}`, // Start IDs from W071
            name: `${brand} ${type} Watch ${i + 1}`,
            brand: brand,
            price: price,
            originalPrice: originalPrice,
            isDiscount: isDiscount,
            type: type,
            color: getRandom(colors),
            caseMaterial: getRandom(caseMaterials),
            strapType: getRandom(strapTypes),
            status: status,
            image: `https://placehold.co/300x300/${Math.floor(Math.random()*16777215).toString(16)}/${Math.floor(Math.random()*16777215).toString(16)}?text=${brand}+${type}`,
            isBestSeller: false,
            description: {
                model: `${brand} ${type} Series`, sku: `SKU${Math.floor(Math.random() * 99999)}`, gender: getRandom(['Men', 'Women', 'Unisex']), style: `${type}, ${getRandom(['Casual', 'Sporty', 'Elegant'])}`,
                movement: getRandom(['Quartz', 'Automatic', 'Digital', 'Eco-Drive']), caliber: `Caliber ${Math.floor(Math.random() * 1000)}`, powerReserve: `${getRandomPrice(24, 90)} hours`, frequency: `${getRandomPrice(18000, 36000)} vph`,
                caseMaterial: getRandom(caseMaterials), caseDiameter: `${getRandomPrice(30, 48)}mm`, caseThickness: `${getRandomPrice(7, 15)}mm`, caseShape: getRandom(['Round', 'Square', 'Octagonal', 'Tonneau']),
                caseCoating: getRandom(['Polished', 'Brushed', 'Matte', 'Ion-Plated', 'N/A']), crystal: getRandom(['Sapphire Crystal', 'Mineral Glass', 'Acrylic', 'Hardlex']), dialColor: getRandom(colors),
                markers: getRandom(['Baton Indexes', 'Arabic Numerals', 'Roman Numerals', 'Dot & Batons', 'Crystals']), lume: getRandom(['Super-LumiNova', 'Chromalight', 'Lumibrite', 'None']), waterResistance: `${getRandomPrice(10, 300)} meters`,
                functions: getRandom(['Date', 'Chronograph', 'GPS', 'Heart Rate', 'Alarm', 'Time Only']), warranty: `${getRandomPrice(1, 5)} Years`, completeness: getRandom(['Original Box, Manual Book', 'Box, Charger Cable', 'Watch Only', 'Box & Papers'])
            },
            comments: []
        });
    }
    return dummyWatches;
}
